import React, { useState, useRef } from 'react';
import axios from 'axios';

import './upload.css';

const FileUploadForm = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [textValue, setTextValue] = useState('');
    const [uploadProgress, setUploadProgress] = useState(0);
    const fileInputRef = useRef(null);

    const handleTextChange = (event) => {
        setTextValue(event.target.value);
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        setSelectedFile(file);
    };

    const handleFormSubmit = async (event) => {
        event.preventDefault();

        if (selectedFile) {
            const formData = new FormData();
            formData.append('pdfFile', selectedFile);
            formData.append('consumerNo', textValue);

            try {
                const response = await axios.post('http://127.0.0.1:8000/user/api/upload-pdf/', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                    onUploadProgress: (progressEvent) => {
                        const progress = Math.round((progressEvent.loaded / progressEvent.total) * 100);
                        setUploadProgress(progress);
                    },
                });

                if (response.status === 200) {
                    alert("PDF Uploaded successfully");
                    setSelectedFile(null);
                    setTextValue('');
                    setUploadProgress(0);
                    // Reset the file input
                    fileInputRef.current.value = null;
                } else if (response.status === 403) {
                    alert("Consumer Number is already present");
                } else {
                    console.error('Failed to upload PDF');
                }
            } catch (error) {
                const status_code = error['response']['status'];
                if (status_code === 403) {
                    alert("Consumer is already present");
                } else {
                    alert("failed to upload PDF");
                }
            }
        }
    };

    return (
        <div className='upload-box'>
            <div className="file-upload-form text-center">
                <div className="form-group d-flex justify-content-center align-items-center">
                    <label htmlFor="text-input" className='p-2 w-5'>Consumer No:</label>
                    <input type="text" id="text-input" className="form-control w-50" value={textValue} onChange={handleTextChange} />
                </div>
                <div className="form-group d-flex justify-content-center align-items-center p-4">
                    <label htmlFor="file-input" className='p-2 w-5 d-flex justify-content-start'>Upload File:</label>
                    {/* Assign ref to input element */}
                    <input ref={fileInputRef} type="file" id="file-input" className="form-control w-50 fileUploadButton" onChange={handleFileChange} />
                </div>
                <div className="form-group">
                    <button className="btn btn-primary" onClick={handleFormSubmit}>Submit</button>
                </div>
                {uploadProgress > 0 && (
                    <div className="progress-group">
                        <p>Upload Progress: {uploadProgress}%</p>
                        <progress value={uploadProgress} max="100" />
                    </div>
                )}
            </div>
        </div>
    );
};

export default FileUploadForm;
