import React,{ useEffect, useState } from "react";

const GetConsumerData = () => {
    const [textValue, setTextValue] = useState('');

    const handleTextChange = (e) => {
        setTextValue(e.target.value);
    }

    const handleFetchPDF = async () => {
        try {
            const response = await axios.get(`http://127.0.0.1:8000/user/api/get-pdf/${consumerNumber}/`, {
                responseType: 'arraybuffer',
            });

            // Create a Blob from the PDF data
            const blob = new Blob([response.data], { type: 'application/pdf' });

            // Create a link element and trigger a download
            const link = document.createElement('a');
            link.href = window.URL.createObjectURL(blob);
            link.download = `${consumerNumber}.pdf`;
            link.click();
        } catch (error) {
            console.error('Error fetching PDF:', error);
        }
    };


    return (
        <div className='upload-box'>
            <h3 className='d-flex justify-content-center pb-5 fw-light'>Get Consumer's Details</h3>
            <div className="file-upload-form text-center">
                <div className="form-group d-flex justify-content-center align-items-center">
                    <label htmlFor="text-input" className='p-2 w-5'>Consumer No:</label>
                    <input type="text" id="text-input" className="form-control w-50" value={textValue} onChange={handleTextChange} />
                </div>
                <div className="form-group">
                    <button className="btn btn-primary mt-5" onClick={handleFetchPDF}>Submit</button>
                </div>
            </div>
        </div>
    );
}

export default GetConsumerData;
