import React from 'react';

import mobile from '../../Illustrations/mobile_phone.png';
import './first_slide.css';

const First_Slide = () => {
    return (
      <div className='container text-center d-flex align-items-center justify-content-center vh-100'>
        <div className='row'>
          <div className='col-md-6 d-flex justify-content-start'>
            <img src={mobile} alt='Image of the first slide' width='400px' height='400px' className='img-fluid first-slide-image' />
          </div>
          <div className='col-md-6 d-flex justify-content-center'>
            <div className='first-slide-text'>
              <h3 className='pb-4'>Pocket Friendly</h3>
              <p>You can access any documents and details from your mobile phone also, thus removing the office desktop dependencies.</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

export default First_Slide;
