import React from 'react';

import mobile from '../../Illustrations/mobile_phone.png';
import './first_slide.css';

const First_Slide = () => {
    return(
        <div class='container text-center vh-100'>
            <div class='row'>
                <div class='col'>
                    <img src={mobile} alt='Image of the first slide' width='400px' height='400px' className='first-slide-image'/>
                </div>
                <div class='col'>
                    <div className='first-slide-text'>
                        <h3 class='pb-2'>Pocket Friendly</h3>
                        <p>You Can access any documents and details from your mobile phone also, thus removing the office desktop dependencies.</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default First_Slide;
