import axios from "axios";
import React, { useState } from "react";
import "./login.css"; // Ensure that your CSS file is correctly linked
import show from "../../Illustrations/show.png";
import hide from "../../Illustrations/hide.png";

const Registration = () => {
  const [password, setPassword] = useState('');
  const [confirmpassword, setConfirmPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmpassword: '',
  });
  const [error, setError] = useState('');

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleToggleConfirmPassword = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.confirmpassword || !formData.email || !formData.password) {
      setError('All fields are required.');
      return;
    }

    if (formData['password'] != formData['confirmpassword']){
      setError("Passwords don't match");
      return
    }

    try {
      const response = await axios.post('http://localhost:8000/api/register/', formData);
      console.log(response.data);
    } catch (error) {
      setError('Registration failed. Please try again.');
      console.error('Registration failed', error);
    }
  };

  return (
    <div>
        <h3>Registration Form</h3>
            <form className="form-style" onSubmit={handleSubmit}>
              {error && <p style={{ color: 'red' }}>{error}</p>}
              <label htmlFor="register-username" className="label-style">
                Email:
              </label>
              <input className="input-style" type="text" id="register-username" name="register-username" onChange={handleChange} required />

              <label className="label-style" htmlFor="register-password">
                Password:
              </label>
              <div className="password-toggle">
                <input type={showPassword ? 'text' : 'password'} className="input-style" id="register-password" name="register-password" value={formData['password']} onChange={handleChange} required />
                <button className="password-button-style" type="button" onClick={handleTogglePassword}>
                  {showPassword ? <img src={show} alt="Show Password" width='20px' height='20px' /> : <img src={hide} alt="Hide Password" width='20px' height='20px' />}
                </button>
              </div>
              <label className="label-style" htmlFor="register-confirmpassword">
                Confirm Password:
              </label>
              <div className="password-toggle">
                <input type={showConfirmPassword ? 'text' : 'password'} className="input-style" id="register-confirmpassword" name="register-confirmpassword" value={formData['confirmpassword']} onChange={handleChange} required />
                <button className="password-button-style" type="button" onClick={handleToggleConfirmPassword}>
                  {showConfirmPassword ? <img src={show} alt="Show Password" width='20px' height='20px' /> : <img src={hide} alt="Hide Password" width='20px' height='20px' />}
                </button>
              </div>

              <button type="submit" className="button-style">
                Submit
              </button>
            </form>
    </div>
  );
};

export default Registration;
