import React, { useState, useEffect } from "react";
import { backend_url } from "../../../Components/configurations";

import { GrPrevious } from 'react-icons/gr';
import { GrCaretNext } from 'react-icons/gr';
import { GrNext } from 'react-icons/gr';

import './History.css';

const History = () => {
    const [rowData, setRowData] = useState({});
    const [start, setStart] = useState(0);
    const [end,setEnd] = useState(10);

    const handleFetch = async () => {
        try {
            const accessToken = localStorage.getItem('access_token');
            const user = localStorage.getItem('user');

            const response = await fetch(`${backend_url}user/account_status/?user=${user}`, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${accessToken}`,
                }
            });
            const data = await response.json();
            setRowData(data);
            console.log(data); // Check fetched data
        } catch (error) {
            console.log('Error occurred', error);
        }
    };

    useEffect(() => {
        handleFetch(); // Call handleFetch when component mounts

    }, []); // Empty dependency array ensures this effect runs only once after initial render

    const consumersTable = rowData.all_rows && rowData.all_rows.slice(start,end).map((row,index) => (
        <tr key={index}>
            <td>{row.consumer_number}</td>
            <td>{row.file_key}</td>
        </tr>
    ))


    const increaseStart = (e) => {
        setStart(start + 10);
    }

    const decreaseStart = (e) => {
        setStart(start - 10);
    }

    const increaseEnd = (e) => {
        setEnd(end + 10);
    }

    const decreaseEnd = (e) => {
        setEnd(end - 10);
    }

    return (
        <div className='history-body'>
            <div class="container">
                <div class="row align-items-center mt-5 mb-2">
                    <div>
                        <p class="fs-2">Total consumers: {rowData.total_count}</p>
                    </div>
                </div>
                <div class="table-style">
                    <table class="table table-secondary border-bottom border-rounded border-0.5 border-light table-hover fw-light">
                        <thead>
                            <tr>
                            <th scope="col">Consumer number</th>
                            <th scope="col">File Key</th>
                            </tr>
                        </thead>
                        <tbody>
                            {consumersTable}
                        </tbody>
                    </table>
                </div>
                <p class="text-50">*Sorted by last modified time</p>
                <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                    { (start > 0) ?
                        (<button onClick={() => {decreaseEnd();decreaseStart();}} class="btn btn-secondary" type='button' ><GrPrevious/></button>) :
                        (<button onClick={() => {decreaseEnd();decreaseStart();}} class="btn btn-secondary disabled" type='button' ><GrPrevious/></button>)
                    }
                    { (end <= rowData.total_count ) ?
                        (<button onClick={() => {increaseEnd();increaseStart();}} class="btn btn-outline-secondary" type='button' ><GrNext/></button>) :
                        (<button onClick={() => {increaseEnd();increaseStart();}} class="btn btn-outline-secondary disabled" type='button' ><GrNext/></button>)
                    }
                </div>
                <hr class="white-line mt-5" />
            </div>
        </div>
    );
};

export default History;
